distortionPreset.code:
seed = (seed == 0) ? srand() * 100 : seed;
progress = seed + d;
t = progress/wobble_size;

firstPressureScale = noise(t);
secondPressureScale = noise(t / second_layer_scale);
first_layer_weight = 1 - second_layer_weight;
combinedNoise = (secondPressureScale * second_layer_weight) + (firstPressureScale * first_layer_weight);

pressureScale = 1 - (wobble_strength_percent * combinedNoise);

op = p == 0 ? 0 : p * pressureScale;
